mapper.update:
update user
set report_cnt = report_cnt + 1
where user_id = #{userId}

mapper.select:
select count (*)
from user
where user_id = #{user_id} AND user_pwd = #{user_pwd}

select count (*)
from user
where user_id = #{user_id} AND user_email = #{user_email} AND user_cp = #{user_cp}

mapper.update:
update user
set user_pwd = #{userPwd}
where user_id = #{userId}

update user
set user_pwd = #{userPwd}, user_nick = #{user_nick}, user_cp = #{userCp}, user_email = #{userEmail}, user_addr = #{userAddr}, user_daddr = #{userDaddr}
where user_id = #{userId}

mapper.select:
select user_status
from user
where user_id = #{userId}

select count (*)
from user
where user_id = #{userId}

select user_grade
from user
where user_id = #{userId}

mapper.update:
update user
set user_status = 'reject'
where user_id = #{userId}

update user
set user_status = 'pass'
where user_id = #{userId}

update user
set user_status = 'pending'
where user_id = #{userId}
